import React, { Component, useState } from "react";
import { Redirect } from "react-router-dom";
import axios from "axios";
import NavBar from "../component/NavBar";
const CandidateRegister = () => {
	const [email, setEmail] = useState();
	const [Fname, setFname] = useState();
	const [LName, setLName] = useState();
	const [Username, setUsername] = useState();
	const [Password, setPassword] = useState();
	const [CPassword, setCPassword] = useState();
	const [DOB, setDOB] = useState();
	const [Qualification, setQualification] = useState();
	const [PassingDate, setPassingDate] = useState();
	const [CurrentCity, setCurrentCity] = useState();
	const [Skills, setSkills] = useState();
	const [Experience, setExperience] = useState();
	const [redirect, setRedirect] = useState(false);
	const [isLoggedin, setLogg] = useState(
		localStorage.getItem("token") == null ? false : true
	);
	

	const SubmitHandle = (e) => {
		e.preventDefault();
		var userObj = {
			email,
			Fname,
			LName,
			Username,
			Password,
			DOB,
			Qualification,
			PassingDate,
			CurrentCity,
			Skills,
			Experience,
		};
		axios
			.post("http://localhost:3001/api/registerCandidate/", userObj)
			.then((response) => {
				if (response) {
					console.log(response);
					setRedirect(true);
				}
			});
	};
	if (isLoggedin) {
		if (localStorage.getItem("type") == "Company")
		   return <Redirect to='/CompanyHome' />;
	   else return <Redirect to='/CompanyHome' />;
   }
   else if (redirect) {
	   debugger;
	   return <Redirect to='/Login' />;
}

	const checkpassword=(pwd)=>{
		if(pwd==Password)
			{setPassword(pwd)
				document.getElementById('passwordSpan').innerHTML=""}
		else
		{
			debugger;
			document.getElementById('passwordSpan').innerHTML="Password doesn't match";
			document.getElementById('passwordSpan').classList.add("myspans");
		}
	}
	
	return (
		<>
			<NavBar />
			<main className='form-signin'>
				<form onSubmit={SubmitHandle}>
					<h1 className='h3 mb-3 fw-normal'>New Job</h1>

					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingInput'
							placeholder='Job Role'
							onChange={(e) => setJobRole(e.target.value)}
							required
						/>

					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Description (minimum 300 words)'
							onChange={(e) => setDescription(e.target.value)}
							required
						/>

					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Location'
							onChange={(e) => setLocation(e.target.value)}
							required
						/>
			
					</div>
					
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Minimum Salary per annum'
							min='100000'
							onChange={(e) => setMinimumSalary(e.target.value)}
							required
						/>
		
					</div>
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Maximum Salary per annum'
							min='100000'
							onChange={(e) => setMaximumSalary(e.target.value)}
							required
						/>
			
					</div>
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Required Experience'
							min='0'
							onChange={(e) => setRequiredExperience(e.target.value)}
							required
						/>
					
					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Skills Required'
							onChange={(e) => setRequiredSkills(e.target.value)}
							required
						/>
					</div>
			
					<button className='w-100 btn btn-lg btn-primary' type='submit'>
						Register
					</button>
					<p className='mt-5 mb-3 text-muted'>&copy; 2021</p>
				</form>
			</main>
		</>
	);
};

export default CandidateRegister;
