import React, { Component, useState } from "react";
import { Redirect } from "react-router-dom";
import axios from "axios";
import NavBar from "../component/NavBar";
const CandidateRegister = () => {
	const [JobRole, setJobRole] = useState();
	const [Description, setDescription] = useState();
	const[Location,setLocation]=useState();
	const[MinimumSalary,setMinimumSalary]=useState();
	const[MaximumSalary,setMaximumSalary]=useState();
	const [RequiredSkills, setRequiredSkills]=useState();
	const [RequiredExperience, setRequiredExperience]=useState()
	const [isLoggedin, setLogg] = useState(
		localStorage.getItem("token") == null ? false : true
	);
	

	const SubmitHandle = (e) => {
		e.preventDefault();
		var userObj = {
			email,
			Fname,
			LName,
			Username,
			Password,
			DOB,
			Qualification,
			PassingDate,
			CurrentCity,
			Skills,
			Experience,
		};
		axios
			.post("http://localhost:3001/api/registerCandidate/", userObj)
			.then((response) => {
				if (response) {
					console.log(response);
					setRedirect(true);
				}
			});
	};
 if (redirect) {
	   debugger;
	   return <Redirect to='/Login' />;
}

	
	const checkConstraints=(passwd)=>{
		let ele=document.getElementById("passwordConstraints");
		let lowerCaseLetters = /[a-z]/g;
		let upperCaseLetters=/[A-Z]/g;
		let Numbers=/[0-9]/g;
		let specialChars=/[$,@,#]/g
		let isValid= true;
		if(passwd.length<8){
			ele.innerHTML="length must be greater than 8";
			isValid=false
		}else
		if(passwd.match(lowerCaseLetters)==null){
			ele.innerHTML="Password should contains small letters"
			isValid=false
		}else
		if(passwd.match(upperCaseLetters)==null){
			ele.innerHTML="Password should contains Capitals letters"
			isValid=false
		}else
		 if(passwd.match(Numbers)==null){
			ele.innerHTML="Password should contains Numbers"
			isValid=false
		}else
		if(passwd.match(specialChars)==null){
			ele.innerHTML="Password should contains special like #,@,$"
			isValid=false
		}else
		if(isValid)
		{
			ele.innerHTML=""
			setPassword(passwd);
		}
		else{
			ele.classList.add("myspans");
		}
		
		

	}

	return (
		<>
			<NavBar />
            <main className='form-signin'>
				<form onSubmit={SubmitHandle}>
					<h1 className='h3 mb-3 fw-normal'>New Job</h1>

					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingInput'
							placeholder='Job Role'
							onChange={(e) => setJobRole(e.target.value)}
							required
						/>

					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Description (minimum 300 words)'
							onChange={(e) => setDescription(e.target.value)}
							required
						/>

					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Location'
							onChange={(e) => setLocation(e.target.value)}
							required
						/>
			
					</div>
					
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Minimum Salary per annum'
							min='100000'
							onChange={(e) => setMinimumSalary(e.target.value)}
							required
						/>
		
					</div>
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Maximum Salary per annum'
							min='100000'
							onChange={(e) => setMaximumSalary(e.target.value)}
							required
						/>
			
					</div>
					<div className='form-floating'>
						<input
							type='number'
							className='form-control'
							id='floatingPassword'
							placeholder='Required Experience'
							min='0'
							onChange={(e) => setRequiredExperience(e.target.value)}
							required
						/>
					
					</div>
					<div className='form-floating'>
						<input
							type='text'
							className='form-control'
							id='floatingPassword'
							placeholder='Skills Required'
							onChange={(e) => setRequiredSkills(e.target.value)}
							required
						/>
					</div>
			
					<button className='w-100 btn btn-lg btn-primary' type='submit'>
						Register
					</button>
					<p className='mt-5 mb-3 text-muted'>&copy; 2021</p>
				</form>
			</main>
			</>
	);
};

export default CandidateRegister;
